package oops;

public class pyramid5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		
		for (int i=1;i<=5;i++)
			
		{
		
			int k=5;
		for (int j=1;j<=6-i;j++)
		
		{
			
		System.out.print(k);
		System.out.print("\t");
	    k--;
		
	   }

		System.out.println("");
		
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
