package oops;

public class starpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          
		
		for (int i=5;i>0;i--)
		{
		
		int k=5;	
			
		for (int j=1;j<=i;j++)
			
		{
			
			
			System.out.print(k);
			
			System.out.print("\t");
			
			k--;
			
		}
			
			System.out.println("");
		
		}
		
		
		
		
	}

}
